module gshare_predictor (
    input clk,
    input areset,

    input predict_valid,
    input [6:0] predict_pc,
    output predict_taken,
    output [6:0] predict_history,

    input train_valid,
    input train_taken,
    input train_mispredicted,
    input [6:0] train_history,
    input [6:0] train_pc
);

    reg [6:0] global_history;
    reg [127:0] pht;
    wire [6:0] index;
    wire [1:0] counter;
    reg [6:0] counter_state;

    // Gshare index calculation
    assign index = predict_pc ^ global_history;

    // PHT counter retrieval
    assign counter = pht[index];

    // Branch prediction
    assign predict_taken = counter[1];
    assign predict_history = global_history;

    // Update global history register
    always @(posedge clk or posedge areset) begin
        if (areset) begin
            global_history <= 7'b0;
        end else if (predict_valid || train_valid) begin
            global_history <= {global_history[5:0], 1'b0};
        end
    end

    // Training logic
    always @(posedge clk or posedge areset) begin
        if (areset) begin
            pht <= 128'b0;
            counter_state <= 7'b0;
        end else if (train_valid) begin
            // Update PHT based on training
            if (train_taken == counter[1]) begin
                // No change if correct prediction
                if (counter != 2'b11) begin
                    pht[index] <= counter + 1;
                end
            end else begin
                // Change direction if incorrect prediction
                if (counter != 2'b00) begin
                    pht[index] <= counter - 1;
                end
            end

            // Update counter state for mispredicted branch
            if (train_mispredicted) begin
                counter_state <= train_history;
            end
        end
    end

    // Update global history register for mispredicted branches
    always @(posedge clk) begin
        if (counter_state != 7'b0 && counter_state != 7'b111) begin
            global_history <= global_history ^ (counter_state[0] ? 7'b111111 : 7'b0);
        end
    end

endmodule

