module top_module(
    input clk,
    input reset,
    input data,
    output reg shift_ena,
    output reg counting,
    input done_counting,
    output reg done,
    input ack
);

    // State encoding
    localparam S_IDLE = 3'b000;
    localparam S_SHIFT = 3'b001;
    localparam S_COUNT = 3'b010;
    localparam S_DONE = 3'b011;
    localparam S_ACK = 3'b100;

    reg [2:0] state, next_state;
    reg [1:0] shift_count;
    reg [3:0] duration;

    // State register
    always @(posedge clk or posedge reset) begin
        if (reset)
            state <= S_IDLE;
        else
            state <= next_state;
    end

    // Next state logic
    always @(*) begin
        next_state = state;
        case (state)
            S_IDLE:
                if (data && !shift_ena)
                    next_state = S_SHIFT;
            S_SHIFT:
                if (shift_count == 2'b11)
                    next_state = S_COUNT;
            S_COUNT:
                if (done_counting)
                    next_state = S_DONE;
            S_DONE:
                next_state = S_ACK;
            S_ACK:
                if (ack)
                    next_state = S_IDLE;
            default:
                next_state = S_IDLE;
        endcase
    end

    // Output logic
    always @(posedge clk or posedge reset) begin
        if (reset) begin
            shift_ena <= 0;
            counting <= 0;
            done <= 0;
            shift_count <= 0;
            duration <= 0;
        end else begin
            case (state)
                S_IDLE: begin
                    shift_ena <= 0;
                    counting <= 0;
                    done <= 0;
                    shift_count <= 0;
                    duration <= 0;
                end
                S_SHIFT: begin
                    shift_ena <= 1;
                    if (shift_count == 2'b11)
                        duration <= {duration[2:0], data};
                    shift_count <= shift_count + 1;
                end
                S_COUNT: begin
                    shift_ena <= 0;
                    counting <= 1;
                end
                S_DONE: begin
                    done <= 1;
                end
                S_ACK: begin
                    done <= 0;
                end
            endcase
        end
    end

endmodule

