module top_module(
    input clk,
    input load,
    input [511:0] data,
    output reg [511:0] q);

    wire [511:0] next_q;

    // Calculate next state for each cell
    genvar i;
    generate
        for (i = 0; i < 512; i = i + 1) begin : rule_90
            assign next_q[i] = q[i-1] ^ q[i] ^ q[i+1];
        end
    endgenerate

    // Update state on clock edge
    always @(posedge clk) begin
        if (load) begin
            q <= data;
        end else begin
            q <= next_q;
        end
    end

    // Handle boundary conditions
    always @(*) begin
        q[-1] = 1'b0;
        q[512] = 1'b0;
    end

endmodule

