reg [3:0] shift_reg;
reg [3:0] timer;
reg [3:0] remaining_time;
reg start_count;

// Detect the start sequence and shift in the duration bits
always @(posedge clk or posedge reset) begin
    if (reset) begin
        shift_reg <= 4'b0000;
        start_count <= 1'b0;
    end
    else begin
        shift_reg <= {shift_reg[2:0], data};
        if (shift_reg == 4'b1101) begin
            shift_reg <= {shift_reg[2:0], data};
            start_count <= 1'b1;
        end
        else begin
            start_count <= 1'b0;
        end
    end
end

// Implement the timer
always @(posedge clk or posedge reset) begin
    if (reset) begin
        timer <= 4'b0000;
        counting <= 1'b0;
        done <= 1'b0;
        remaining_time <= 4'b0000;
    end
    else begin
        if (start_count) begin
            timer <= {shift_reg[3:0]} + 4'b0001;
            counting <= 1'b1;
            done <= 1'b0;
            remaining_time <= {shift_reg[3:0]} + 4'b0001;
        end
        else if (counting) begin
            if (timer == 4'b0000) begin
                counting <= 1'b0;
                done <= 1'b1;
            end
            else begin
                timer <= timer - 4'b0001;
                remaining_time <= remaining_time - 4'b0001;
            end
        end
        else begin
            done <= 1'b0;
            remaining_time <= 4'b0000;
        end
    end
end

// Connect the count output to the remaining_time signal
assign count = remaining_time;

