module water_level_controller (
    input clk,
    input reset,
    input [3:1] s,
    output reg fr3,
    output reg fr2,
    output reg fr1,
    output reg dfr
);

    // State definitions
    localparam [2:0] LOWEST_SENSOR = 3'b001,
                        SENSOR_ONE = 3'b010,
                        SENSOR_TWO = 3'b011,
                        HIGHEST_SENSOR = 3'b100;

    // State register
    reg [2:0] current_state, next_state;

    // Flow rate lookup table
    reg [3:0] nominal_flow_rate [0:3];
    initial begin
        nominal_flow_rate[0] = 4'b0000;
        nominal_flow_rate[1] = 4'b0001;
        nominal_flow_rate[2] = 4'b0010;
        nominal_flow_rate[3] = 4'b0100;
    end

    // State transition logic
    always @(posedge clk or posedge reset) begin
        if (reset)
            current_state <= LOWEST_SENSOR;
        else
            current_state <= next_state;
    end

    // Next state and output logic
    always @(*) begin
        case (current_state)
            LOWEST_SENSOR: begin
                if (s[1]) next_state = SENSOR_ONE;
                else next_state = LOWEST_SENSOR;
                fr3 = 1;
                fr2 = 1;
                fr1 = 1;
                dfr = 0;
            end
            SENSOR_ONE: begin
                if (s[2]) next_state = SENSOR_TWO;
                else if (s[1]) next_state = SENSOR_ONE;
                else next_state = LOWEST_SENSOR;
                fr3 = 1;
                fr2 = 1;
                fr1 = nominal_flow_rate[1][3];
                dfr = 0;
            end
            SENSOR_TWO: begin
                if (s[3]) next_state = HIGHEST_SENSOR;
                else if (s[2]) next_state = SENSOR_TWO;
                else next_state = SENSOR_ONE;
                fr3 = 1;
                fr2 = nominal_flow_rate[2][3];
                fr1 = nominal_flow_rate[2][2];
                dfr = 0;
            end
            HIGHEST_SENSOR: begin
                next_state = HIGHEST_SENSOR;
                fr3 = nominal_flow_rate[3][3];
                fr2 = nominal_flow_rate[3][2];
                fr1 = nominal_flow_rate[3][1];
                dfr = 1;
            end
            default: begin
                next_state = LOWEST_SENSOR;
                fr3 = 1;
                fr2 = 1;
                fr1 = 1;
                dfr = 0;
            end
        endcase
    end

endmodule

