module top_module (
    input clk,
    input in,
    input reset,
    output done
);

reg [3:0] state;
reg [2:0] bit_count;
reg [7:0] data;

localparam IDLE = 4'b0000;
localparam START = 4'b0001;
localparam DATA = 4'b0010;
localparam STOP = 4'b0011;
localparam DONE = 4'b0100;

always @(posedge clk or posedge reset) begin
    if (reset) begin
        state <= IDLE;
        bit_count <= 0;
        data <= 0;
    end
    else begin
        case (state)
            IDLE: begin
                if (~in) state <= START;
            end
            START: begin
                state <= DATA;
                bit_count <= 0;
            end
            DATA: begin
                data <= {in, data[7:1]};
                bit_count <= bit_count + 1;
                if (bit_count == 7) state <= STOP;
            end
            STOP: begin
                if (in) state <= DONE;
                else state <= IDLE;
            end
            DONE: begin
                state <= IDLE;
            end
        endcase
    end
end

assign done = (state == DONE);

endmodule

module top_module (
    input clk,
    input in,
    input reset,
    output reg [7:0] out_byte,
    output reg done
);

    // State encoding
    localparam IDLE = 3'b000;
    localparam START = 3'b001;
    localparam DATA = 3'b010;
    localparam STOP = 3'b011;

    reg [2:0] state, next_state;
    reg [2:0] bit_count;
    reg [7:0] shift_reg;

    always @(posedge clk or posedge reset) begin
        if (reset) begin
            state <= IDLE;
            bit_count <= 0;
            shift_reg <= 8'b0;
            out_byte <= 8'b0;
            done <= 0;
        end else begin
            state <= next_state;
            case (state)
                DATA: begin
                    shift_reg <= {in, shift_reg[7:1]};
                    bit_count <= bit_count + 1;
                end
                STOP: begin
                    out_byte <= shift_reg;
                    done <= 1;
                    bit_count <= 0;
                end
                default: begin
                    done <= 0;
                end
            endcase
        end
    end

    always @(*) begin
        next_state = state;
        case (state)
            IDLE: begin
                if (in == 0) next_state = START;
            end
            START: begin
                next_state = DATA;
            end
            DATA: begin
                if (bit_count == 7 && in == 1) next_state = STOP;
            end
            STOP: begin
                next_state = IDLE;
            end
        endcase
    end

endmodule

