module top_module(
    input clk,
    input areset, 

    input predict_valid,
    input [6:0] predict_pc,
    output predict_taken, 
    output [6:0] predict_history,

    input train_valid,
    input train_taken,
    input train_mispredicted,
    input [6:0] train_history, 
    input [6:0] train_pc
);

  // 7-bit global history register
  reg [6:0] global_history;
  reg [6:0] global_history_updated;

  // 128-entry 2-bit saturating counter table
  reg [1:0] prediction_table [127:0];

  // 7-bit index for the table
  wire [6:0] index = global_history ^ predict_pc;

  // Prediction logic
  assign predict_taken = prediction_table[index][1];
  assign predict_history = global_history;

  // Training logic
  integer i;
  always @(posedge clk or posedge areset) begin
    if (areset) begin
      for (i = 0; i < 128; i = i + 1) begin
        prediction_table[i] <= 2'b00;
      end
      global_history <= 7'b0000000;
    end else begin
      // Update global history
      global_history <= global_history_updated;

      if (train_valid) begin
        // Update PHT based on training
        if (train_taken) begin
          if (prediction_table[index] < 2'b11)
            prediction_table[index] <= prediction_table[index] + 1;
        end else begin
          if (prediction_table[index] > 2'b00)
            prediction_table[index] <= prediction_table[index] - 1;
        end

        // Update global history for mispredicted branch
        if (train_mispredicted) begin
          global_history_updated <= train_history;
        end else begin
          global_history_updated <= global_history;
        end
      end else begin
        global_history_updated <= global_history;
      end
    end
  end

endmodule

