module top_module (
    input clk,
    input areset,
    input bump_left,
    input bump_right,
    input ground,
    input dig,
    output walk_left,
    output walk_right,
    output aaah,
    output digging
);

reg [3:0] state, next_state;
parameter WALK_LEFT = 4'b0001, WALK_RIGHT = 4'b0010, FALL_LEFT = 4'b0100, FALL_RIGHT = 4'b1000, DIG_LEFT = 4'b0110, DIG_RIGHT = 4'b1010, SPLAT = 4'b1111;

always @(posedge clk or posedge areset) begin
    if (areset)
        state <= WALK_LEFT;
    else
        state <= next_state;
end

always @(*) begin
    next_state = state;
    case (state)
        WALK_LEFT: begin
            if (bump_left)
                next_state = WALK_RIGHT;
            else if (!ground)
                next_state = FALL_LEFT;
        end
        WALK_RIGHT: begin
            if (bump_right)
                next_state = WALK_LEFT;
            else if (!ground)
                next_state = FALL_RIGHT;
        end
        FALL_LEFT: begin
            if (ground)
                next_state = WALK_LEFT;
            else if (dig && ground)
                next_state = DIG_LEFT;
        end
        FALL_RIGHT: begin
            if (ground)
                next_state = WALK_RIGHT;
            else if (dig && ground)
                next_state = DIG_RIGHT;
        end
        DIG_LEFT: begin
            if (!ground)
                next_state = FALL_LEFT;
        end
        DIG_RIGHT: begin
            if (!ground)
                next_state = FALL_RIGHT;
        end
        SPLAT: state = SPLAT;
    endcase
    if (ground)
        next_state = WALK_LEFT;
    if (!ground && state != SPLAT)
        next_state = FALL_LEFT;
end

assign walk_left = (state == WALK_LEFT);
assign walk_right = (state == WALK_RIGHT);
assign aaah = (state == FALL_LEFT) || (state == FALL_RIGHT);
assign digging = (state == DIG_LEFT) || (state == DIG_RIGHT);

endmodule

