module top_module (
    input clk,
    input areset,
    input bump_left,
    input bump_right,
    input ground,
    output reg walk_left,
    output reg walk_right,
    output reg aaah
);

    // State encoding
    localparam WALK_LEFT = 2'b00;
    localparam WALK_RIGHT = 2'b01;
    localparam FALL = 2'b10;

    reg [1:0] state, next_state;

    // State transition and output logic
    always @(*) begin
        next_state = state;
        walk_left = 0;
        walk_right = 0;
        aaah = 0;

        case (state)
            WALK_LEFT: begin
                walk_left = 1;
                if (bump_left && ground)
                    next_state = WALK_RIGHT;
                else if (!ground)
                    next_state = FALL;
            end

            WALK_RIGHT: begin
                walk_right = 1;
                if (bump_right && ground)
                    next_state = WALK_LEFT;
                else if (!ground)
                    next_state = FALL;
            end

            FALL: begin
                aaah = 1;
                if (ground)
                    next_state = bump_left ? WALK_RIGHT : WALK_LEFT;
            end

            default: next_state = WALK_LEFT;
        endcase
    end

    // State register
    always @(posedge clk or posedge areset) begin
        if (areset)
            state <= WALK_LEFT;
        else
            state <= next_state;
    end

