module top_module (
    input clk,
    input resetn,
    input [3:1] r,
    output [3:1] g
);

// state table
reg [2:0] state, next_state;
parameter A = 3'b000, B = 3'b001, C = 3'b010, D = 3'b011;

always @(*) begin
    case (state)
        A: begin
            if (r[1] == 1) next_state = B;
            else if (r[2] == 1) next_state = C;
            else if (r[3] == 1) next_state = D;
            else next_state = A;
        end
        B: begin
            if (r[1] == 1) next_state = B;
            else next_state = A;
        end
        C: begin
            if (r[2] == 1) next_state = C;
            else next_state = A;
        end
        D: next_state = A;
        default: next_state = A;
    endcase
end

// state flip-flops
always @(posedge clk or negedge resetn) begin
    if (!resetn)
        state <= A;
    else
        state <= next_state;
end

// output logic
assign g[1] = (state == B);
assign g[2] = (state == C);
assign g[3] = (state == D);

endmodule

