module top_module(
    input clk,
    input load,
    input [511:0] data,
    output reg [511:0] q);

    wire [511:0] next_q;

    // 2-to-1 multiplexer
    assign next_q = load ? data : {q[510:0], q[0] ^ q[1]};

    // 512-bit wide register
    always @(posedge clk) begin
        q <= next_q;
    end

    // Combinational circuit to calculate next state
    genvar i;
    generate
        for (i = 0; i < 512; i = i + 1) begin : gen_next_state
            if (i == 0) begin
                assign q[i] = q[511] ^ q[0];
            end
            else if (i == 511) begin
                assign q[i] = q[510] ^ q[0];
            end
            else begin
                assign q[i] = q[i-1] ^ q[i+1];
            end
        end
    endgenerate

