module top_module (
    input clk,
    input in,
    input reset,
    output reg out
);

    // State encoding
    localparam A = 1'b0;
    localparam B = 1'b1;

    // State register
    reg state, next_state;

    // State transition and output logic
    always @(*) begin
        case (state)
            A: begin
                next_state = in ? A : B;
                out = 1'b0;
            end
            B: begin
                next_state = in ? B : A;
                out = 1'b1;
            end
            default: begin
                next_state = B;
                out = 1'b1;
            end
        endcase
    end

    // State register update
    always @(posedge clk) begin
        if (reset)
            state <= B;
        else
            state <= next_state;
    end

endmodule

