                    next_state = S0;
                else
                    next_state = S0;
            end
            default: next_state = S0;
        endcase
    end

    assign disc = (state == S2) & (in == 1'b0);
    assign flag = (state == S2) & (in == 1'b1);
    assign err = (state == S2) & (in == 1'b1);

