module top_module (
    input clk,
    input reset,
    input [3:1] s,
    output reg fr3,
    output reg fr2,
    output reg fr1,
    output reg dfr
);

    // Parameters for flow rates
    parameter fr3 = 3'b100;
    parameter fr2 = 3'b010;
    parameter fr1 = 3'b001;
    parameter dfr = 1'b1;

    // State definition
    reg [2:0] state, next_state;
    parameter [2:0] S0 = 3'b000, S1 = 3'b001, S2 = 3'b010, S3 = 3'b011, S4 = 3'b100;

    // State register
    always @(posedge clk or posedge reset) begin
        if (reset)
            state <= S4;
        else
            state <= next_state;
    end

    // Next state logic
    always @(*) begin
        case (state)
            S0: next_state = (s[1]) ? S1 : S0;
            S1: next_state = (s[2]) ? S2 : S1;
            S2: next_state = (s[3]) ? S3 : S2;
            S3: next_state = (s[3]) ? S3 : S4;
            S4: next_state = (s[1]) ? S4 : S0;
            default: next_state = S0;
        endcase
    end

    // Output logic
    always @(*) begin
        case (state)
            S0: begin
                fr3 = 1'b0; fr2 = 1'b0; fr1 = 1'b0; dfr = 1'b0;
