module gshare_branch_predictor(
    input clk,
    input areset,

    input predict_valid,
    input [6:0] predict_pc,
    output predict_taken,
    output [6:0] predict_history,

    input train_valid,
    input train_taken,
    input train_mispredicted,
    input [6:0] train_history,
    input [6:0] train_pc
);

    reg [6:0] global_history_reg;
    reg [127:0][1:0] pattern_history_table;

    wire [6:0] index;
    assign index = predict_pc ^ global_history_reg;

    always @(posedge clk or posedge areset) begin
        if (areset) begin
            global_history_reg <= 7'b0;
            pattern_history_table <= 128'b0;
        end else begin
            if (train_valid) begin
                if (train_mispredicted) begin
                    global_history_reg <= train_history;
                end
                if (train_valid) begin
                    pattern_history_table[train_history] <= (train_taken) ? 2'b10 : 2'b01;
                end
            end
            if (predict_valid) begin
                if (train_valid && train_history == index && !train_mispredicted) begin
                    global_history_reg <= train_history;
                end
                global_history_reg <= {global_history_reg[5:0], train_taken};
            end
        end
    end

    assign predict_taken = (pattern_history_table[index] == 2'b11) || (pattern_history_table[index] == 2'b10 && global_history_reg[6]);
    assign predict_history = global_history_reg;

endmodule

