module top_module (
    input clk,
    input areset,
    input bump_left,
    input bump_right,
    input ground,
    input dig,
    output walk_left,
    output walk_right,
    output aaah,
    output digging
);

reg walk_left_reg, walk_right_reg;
reg aaah_reg, digging_reg;
reg [1:0] counter;
reg falling;

always @(posedge clk or posedge areset) begin
    if (areset) begin
        walk_left_reg <= 1'b1;
        walk_right_reg <= 1'b0;
        aaah_reg <= 1'b0;
        digging_reg <= 1'b0;
        counter <= 2'b00;
        falling <= 1'b0;
    end
    else begin
        // Walk left or right
        if (ground && !falling) begin
            if (bump_left) begin
                walk_left_reg <= 1'b0;
                walk_right_reg <= 1'b1;
            end
            else if (bump_right) begin
                walk_left_reg <= 1'b1;
                walk_right_reg <= 1'b0;
            end
        end

        // Fall or dig
        if (!ground) begin
            falling <= 1'b1;
            if (dig) begin
                digging_reg <= 1'b1;
            end
        end
        else begin
            falling <= 1'b0;
            digging_reg <= 1'b0;
        end

        // Counter for falling
        if (falling) begin
            if (ground) begin
                counter <= 2'b00;
            end
            else if (counter < 2'b10) begin
                counter <= counter + 1'b1;
            end
        end

        // Splatter condition
        if (counter == 2'b10 && ground) begin
            walk_left_reg <= 1'b0;
            walk_right_reg <= 1'b0;
            aaah_reg <= 1'b0;
            digging_reg <= 1'b0;
        end

        // Aaah!
        if (falling && !ground) begin
            aaah_reg <= 1'b1;
        end
        else begin
            aaah_reg <= 1'b0;
        end
    end
end

assign walk_left = walk_left_reg;
assign walk_right = walk_right_reg;
assign aaah = aaah_reg;
assign digging = digging_reg;

endmodule

