module top_module(
    input clk,
    input reset,
    input data,
    output reg shift_ena,
    output reg counting,
    input done_counting,
    output reg done,
    input ack );

    localparam IDLE = 2'b00;
    localparam SHIFT = 2'b01;
    localparam WAIT = 2'b10;
    localparam DONE = 2'b11;

    reg [1:0] state, next_state;
    reg [3:0] pattern;
    reg [2:0] bit_counter;

    always @(posedge clk or posedge reset) begin
        if (reset) begin
            state <= IDLE;
            pattern <= 4'b0000;
            bit_counter <= 3'b000;
            shift_ena <= 1'b0;
            counting <= 1'b0;
            done <= 1'b0;
        end else begin
            state <= next_state;
            if (state == SHIFT) begin
                pattern <= {pattern[2:0], data};
                bit_counter <= bit_counter + 1'b1;
            end else if (state == WAIT) begin
                shift_ena <= 1'b0;
                counting <= 1'b1;
            end else if (state == DONE) begin
                done <= 1'b1;
                counting <= 1'b0;
            end else if (state == IDLE) begin
                pattern <= 4'b0000;
                bit_counter <= 3'b000;
                shift_ena <= 1'b0;
                counting <= 1'b0;
                done <= 1'b0;
            end
        end
    end

    always @(*) begin
        case (state)
            IDLE: begin
                if (pattern == 4'b1101) begin
                    next_state = SHIFT;
                end else begin
                    next_state = IDLE;
                end
            end
            SHIFT: begin
                if (bit_counter == 3'b000) begin
                    next_state = WAIT;
                end else begin
                    next_state = SHIFT;
                end
            end
            WAIT: begin
                if (done_counting) begin
                    next_state = DONE;
                end else begin
                    next_state = WAIT;
                end
            end
            DONE: begin
                if (ack) begin
                    next_state = IDLE;
                end else begin
                    next_state = DONE;
                end
            end
            default: next_state = IDLE;
        endcase
    end

endmodule

