module top_module (
    input clk,
    input [7:0] in,
    input reset,
    output [23:0] out_bytes,
    output done
);

  reg [2:0] state;
  reg [7:0] byte_count;
  reg [7:0] byte_1, byte_2, byte_3;

  localparam IDLE = 3'b000,
             WAIT = 3'b001,
             RECEIVE = 3'b010,
             OUTPUT = 3'b011;

  always @(posedge clk or posedge reset) begin
    if (reset) begin
      state <= IDLE;
      byte_count <= 0;
      byte_1 <= 0;
      byte_2 <= 0;
      byte_3 <= 0;
    end else begin
      case (state)
        IDLE: begin
          if (in[7]) begin
            state <= WAIT;
            byte_1 <= in;
            byte_count <= 1;
          end
        end
        WAIT: begin
          if (!in[7]) begin
            state <= RECEIVE;
            byte_2 <= in;
            byte_count <= 2;
          end
        end
        RECEIVE: begin
          if (byte_count == 2) begin
            byte_3 <= in;
            state <= OUTPUT;
          end else begin
            state <= IDLE;
          end
        end
        OUTPUT: begin
          state <= IDLE;
          byte_count <= 0;
        end
      endcase
    end
  end

  assign out_bytes = {byte_1, byte_2, byte_3};
  assign done = (state == OUTPUT);

endmodule

