module top_module (
    input [7:0] a,
    input [7:0] b,
    output [7:0] s,
    output overflow
);

    wire [7:0] sum;
    wire c_out;
    wire cin;
    wire xor_result;

    assign xor_result = a[7] ^ b[7];
    assign cin = xor_result;

    cla_adder cla (
        .a(a[6:0]),
        .b(b[6:0]),
        .cin(cin),
        .sum(sum[6:0]),
        .cout(c_out)
    );

    assign sum[7] = xor_result;
    assign s = sum;

    assign overflow = (a[7] & b[7] & ~sum[7]) | (~a[7] & ~b[7] & sum[7]);

endmodule

