module top_module(
    input clk,
    input areset,
    input train_valid,
    input train_taken,
    output logic [1:0] state
);

    // State encoding
    localparam WEAKLY_NOT_TAKEN = 2'b01;
    localparam WEAKLY_TAKEN = 2'b10;
    localparam STRONGLY_TAKEN = 2'b11;

    // Combinational logic for state transitions
    always @(*) begin
        if (!train_valid) begin
            // When not training, maintain current state
            state = state;
        end else if (train_taken) begin
            // When training and taken, increment state
            if (state != STRONGLY_TAKEN)
                state = state + 1;
        end else begin
            // When training and not taken, decrement state
            if (state != WEAKLY_NOT_TAKEN)
                state = state - 1;
        end
    end

    // Sequential logic for asynchronous reset
    always @(posedge clk or posedge areset) begin
        if (areset) begin
            // Reset to weakly not taken state
            state <= WEAKLY_NOT_TAKEN;
        end else begin
            // Maintain current state during normal operation
            state <= state;
        end
    end

endmodule

