```verilog
module top_module (
    input clk,
    input reset,
    input in,
    output reg disc,
    output reg flag,
    output reg err);

    reg [2:0] state;
    reg [3:0] ones_count;

    localparam IDLE = 3'b000;
    localparam FLAG = 3'b001;
    localparam DISC = 3'b010;
    localparam ERR = 3'b011;

    always @(posedge clk) begin
        if (reset) begin
            state <= IDLE;
            ones_count <= 4'b0;
            disc <= 1'b0;
            flag <= 1'b0;
            err <= 1'b0;
        end else begin
            case (state)
                IDLE: begin
                    if (in == 1'b1) begin
                        state <= FLAG;
                        ones_count <= 4'b1;
                    end else begin
                        state <= IDLE;
                        ones_count <= 4'b0;
                    end
                    disc <= 1'b0;
                    flag <= 1'b0;
                    err <= 1'b0;
                end
                FLAG: begin
                    if (in == 1'b1) begin
                        state <= FLAG;
                        ones_count <= ones_count + 1;
                        if (ones_count == 4'b0111) begin
                            disc <= 1'b1;
                        end
                    end else begin
                        state <= IDLE;
                        ones_count <= 4'b0;
                        disc <= 1'b0;
                        flag <= 1'b1;
                    end
                    err <= 1'b0;
                end
                DISC: begin
                    state <= IDLE;
                    ones_count <= 4'b0;
                    disc <= 1'b0;
                    flag <= 1'b0;
                    err <= 1'b0;
                end
                ERR: begin
                    state <= IDLE;
                    ones_count <= 4'b0;
                    disc <= 1'b0;
                    flag <= 1'b0;
                    err <= 1'b1;
                end
            endcase

            if (in == 1'b1) begin
                if (ones_count == 4'b0111) begin
                    state <= DISC;
                    err <= 1'b1;
                end
            end
        end
    end

endmodule

