module top_module(
    input wire clk,
    input wire reset,
    input wire data,
    output wire [3:0] count,
    output reg counting,
    output reg done,
    input wire ack );

reg [3:0] state;
reg [3:0] delay;
reg [12:0] counter;
reg [3:0] count_reg;

localparam IDLE = 4'b0000;
localparam S1 = 4'b0001;
localparam S2 = 4'b0010;
localparam S3 = 4'b0011;
localparam S4 = 4'b0100;
localparam COUNTING = 4'b0101;
localparam DONE = 4'b0110;

always @(posedge clk or posedge reset) begin
    if (reset) begin
        state <= IDLE;
        counting <= 0;
        done <= 0;
        delay <= 0;
        counter <= 0;
        count_reg <= 0;
    end else begin
        case (state)
            IDLE: begin
                if (data == 1) begin
                    state <= S1;
                    counting <= 0;
                    done <= 0;
                end
