                        count_reg <= count_reg - 1;
                    end
                end
            end
            DONE: begin
                done <= 1;
                if (ack) begin
                    state <= IDLE;
                    done <= 0;
                end
            end
            default: state <= IDLE;
        endcase
    end
end

