module gshare_branch_predictor (
    input clk,
    input areset,

    input predict_valid,
    input [6:0] predict_pc,
    output predict_taken,
    output [6:0] predict_history,

    input train_valid,
    input train_taken,
    input train_mispredicted,
    input [6:0] train_history,
    input [6:0] train_pc
);

    reg [127:0] pattern_history_table;
    reg [6:0] branch_history_register;
    wire [6:0] index;

    // Index calculation
    assign index = predict_pc ^ branch_history_register;

    // Prediction logic
    always @(*) begin
        if (predict_valid) begin
            case (pattern_history_table[index][1:0])
                2'b00: predict_taken = 0;
                2'b01: predict_taken = 1;
                2'b10: predict_taken = 1;
                2'b11: predict_taken = 0;
            endcase
            predict_history = branch_history_register;
        end else begin
            predict_taken = 0;
            predict_history = 0;
        end
    end

    // Training logic
    always @(posedge clk or posedge areset) begin
        if (areset) begin
            pattern_history_table <= 0;
            branch_history_register <= 0;
        end else begin
            if (train_valid) begin
                // Update pattern history table
                case (pattern_history_table[index][1:0])
                    2'b00: pattern_history_table[index][1:0] <= train_taken ? 2'b01 : 2'b00;
                    2'b01: pattern_history_table[index][1:0] <= 2'b10;
                    2'b10: pattern_history_table[index][1:0] <= train_taken ? 2'b10 : 2'b01;
                    2'b11: pattern_history_table[index][1:0] <= train_taken ? 2'b11 : 2'b10;
                endcase

                // Update branch history register
                branch_history_register <= {branch_history_register[5:0], train_taken};

                // Handle misprediction recovery
                if (train_mispredicted) begin
                    branch_history_register <= train_history;
                end
            end
        end
    end

endmodule

