module lemmings_machine (
    input clk,
    input areset,
    input bump_left,
    input bump_right,
    input ground,
    input dig,
    output walk_left,
    output walk_right,
    output aaah,
    output digging
);

  // Define states
  localparam WALK_LEFT = 2'b00;
  localparam WALK_RIGHT = 2'b01;
  localparam FALL = 2'b10;
  localparam SPLAT = 2'b11;

  // State register
  reg [1:0] state, next_state;

  // Counter for falling time
  reg [4:0] fall_count;

  // State transition logic
  always @(posedge clk or posedge areset) begin
    if (areset)
      state <= WALK_LEFT;
    else
      state <= next_state;
  end

  // Next state logic
  always @(*) begin
    case (state)
      WALK_LEFT: begin
        if (!ground)
          next_state = FALL;
        else if (bump_left && bump_right)
          next_state = WALK_RIGHT;
        else if (bump_left)
          next_state = WALK_RIGHT;
        else if (bump_right)
          next_state = WALK_LEFT;
        else
          next_state = WALK_LEFT;
      end
      WALK_RIGHT: begin
        if (!ground)
          next_state = FALL;
        else if (bump_left && bump_right)
          next_state = WALK_LEFT;
        else if (bump_left)
          next_state = WALK_LEFT;
        else if (bump_right)
          next_state = WALK_RIGHT;
        else
          next_state = WALK_RIGHT;
      end
      FALL: begin
        if (ground)
          next_state = WALK_LEFT;
        else
          next_state = FALL;
      end
      SPLAT: next_state = SPLAT;
      default: next_state = WALK_LEFT;
    endcase
  end

  // Output logic
  assign walk_left = (state == WALK_LEFT);
  assign walk_right = (state == WALK_RIGHT);
  assign aaah = (state == FALL);
  assign digging = (state == WALK_LEFT && ground && dig) || (state == WALK_RIGHT && ground && dig);

  // Falling time counter
  always @(posedge clk or posedge areset) begin
    if (areset)
      fall_count <= 0;
    else if (state == FALL)
      fall_count <= fall_count + 1;
    else
      fall_count <= 0;
  end

  // SPLAT condition
  assign SPLAT = (fall_count == 5'b11111);
endmodule

