module top_module (
    input clk,
    input areset,
    input x,
    output z
);

    reg state;
    wire next_state;
    wire complement;

    // D flip-flop with asynchronous reset
    always @(posedge clk or posedge areset) begin
        if (areset)
            state <= 1'b0;
        else
            state <= next_state;
    end

    // Combinational logic for next state
    assign next_state = x ? ~state : state;

    // Combinational logic for 2's complement
    assign complement = state ? 1'b1 : 1'b0;

    // Output logic
    assign z = complement;

endmodule

