```verilog
module top_module (
    input clk,
    input [7:0] in,
    input reset,
    output done
);

reg [1:0] state;
reg [1:0] next_state;
reg [1:0] byte_count;

parameter IDLE = 2'b00;
parameter WAIT_FIRST = 2'b01;
parameter WAIT_SECOND = 2'b10;
parameter WAIT_THIRD = 2'b11;

always @(posedge clk) begin
    if (reset) begin
        state <= IDLE;
        byte_count <= 2'b00;
    end
    else begin
        state <= next_state;
        if (state == WAIT_FIRST && in[3] == 1'b1)
            byte_count <= 2'b01;
        else if (state != IDLE && byte_count != 2'b11)
            byte_count <= byte_count + 1'b1;
    end
end

always @(*) begin
    case (state)
        IDLE: begin
            if (in[3] == 1'b1)
                next_state = WAIT_FIRST;
            else
                next_state = IDLE;
        end
        WAIT_FIRST: begin
            if (in[3] == 1'b1)
                next_state = WAIT_SECOND;
            else
                next_state = WAIT_FIRST;
        end
        WAIT_SECOND: begin
            if (in[3] == 1'b1)
                next_state = WAIT_THIRD;
            else
                next_state = WAIT_FIRST;
        end
        WAIT_THIRD: begin
            if (byte_count == 2'b11)
                next_state = IDLE;
            else
                next_state = WAIT_FIRST;
        end
        default: next_state = IDLE;
    endcase
end

assign done = (state == WAIT_THIRD) && (byte_count == 2'b11);

endmodule

