module top_module (
    input a, 
    input b,
    input c,
    input d,
    output out
);

    wire w1, w2, w3, w4;

    assign w1 = (a & b) | (~a & ~b & c) | (c & d);
    assign w2 = (a & ~b) | (~a & b & ~c) | (~c & ~d);
    assign w3 = (b & c) | (~a & ~b & ~c & d) | (a & ~b & ~c & ~d);
    assign w4 = (a & ~b & d) | (~a & b & ~c & ~d);

    assign out = w1 | w2 | w3 | w4;

endmodule

module top_module (
    input clk,
    input reset,
    input s,
    input w,
    output reg z
);

reg [2:0] state;
reg [1:0] ones_count;

parameter A = 3'b000, B = 3'b001, C = 3'b010, D = 3'b011, E = 3'b100;

always @(posedge clk or posedge reset) begin
    if (reset) begin
        state <= A;
        ones_count <= 2'b00;
        z <= 1'b0;
    end else begin
        case (state)
            A: begin
                state <= s ? B : A;
                z <= 1'b0;
                ones_count <= 2'b00;
            end
            B, C, D, E: begin
                if (w) ones_count <= ones_count + 1;
                state <= (state == E) ? B : state + 1;
                if (state != E) z <= 1'b0;
            end
            default: state <= A;
        endcase

        if (state == E) begin
            z <= (ones_count == 2'b10);
            ones_count <= 2'b00;
        end
    end
end

endmodule

