module hdlc_framing (
    input clk,
    input reset,
    input in,
    output disc,
    output flag,
    output err
);

// Declare state variables and logic signals
reg [2:0] state;
reg [2:0] next_state;

// Define state encoding
localparam IDLE = 3'b000;
localparam S1 = 3'b001;
localparam S2 = 3'b010;
localparam S3 = 3'b011;
localparam S4 = 3'b100;
localparam S5 = 3'b101;

// State transition logic
always @(posedge clk or posedge reset) begin
    if (reset) begin
        state <= IDLE;
    end else begin
        state <= next_state;
    end
end

// Next state logic
always @(*) begin
    case (state)
        IDLE: begin
            if (in == 1'b0) begin
                next_state = IDLE;
            end else if (in == 1'b1) begin
                next_state = S1;
            end
        end
        S1: begin
            if (in == 1'b0) begin
                next_state = IDLE;
            end else if (in == 1'b1) begin
                next_state = S2;
            end
        end
        S2: begin
            if (in == 1'b0) begin
                next_state = IDLE;
            end else if (in == 1'b1) begin
                next_state = S3;
            end
        end
        S3: begin
            if (in == 1'b0) begin
                next_state = IDLE;
            end else if (in == 1'b1) begin
                next_state = S4;
            end
        end
        S4: begin
            if (in == 1'b0) begin
                next_state = IDLE;
            end else if (in == 1'b1) begin
                next_state = S5;
            end
        end
        S5: begin
            if (in == 1'b0) begin
                next_state = IDLE;
            end else if (in == 1'b1) begin
                next_state = S1; // Error case
            end
        end
        default: begin
            next_state = IDLE;
        end
    endcase
end

// Output logic
assign disc = (state == S5 && in == 1'b0);
assign flag = (state == S5 && in == 1'b1);
assign err = (state == S5 && in == 1'b1);

endmodule

