module gshare_branch_predictor (
    input clk,
    input areset,

    input predict_valid,
    input [6:0] predict_pc,
    output predict_taken,
    output [6:0] predict_history,

    input train_valid,
    input train_taken,
    input train_mispredicted,
    input [6:0] train_history,
    input [6:0] train_pc
);

    reg [1:0] pht [127:0];
    reg [6:0] global_history;

    wire [6:0] index;
    assign index = predict_pc ^ global_history;

    reg [6:0] predicted_history;
    reg [1:0] counter;
    reg prediction;

    always @(posedge clk or posedge areset) begin
        if (areset) begin
            global_history <= 7'b0;
            predicted_history <= 7'b0;
            counter <= 2'b00;
            prediction <= 1'b0;
        end else begin
            if (predict_valid) begin
                counter <= pht[index];
                prediction <= counter[1];
                predicted_history <= global_history;
            end

            if (train_valid) begin
                global_history <= {global_history[5:0], train_history[0]};
            end

            if (train_valid && train_mispredicted) begin
                global_history <= {global_history[5:0], train_history[0]};
            end

            if (train_valid) begin
                if (train_taken == prediction) begin
                    case (pht[index])
                        2'b00: pht[index] <= 2'b01;
                        2'b01: pht[index] <= 2'b10;
                        2'b10: pht[index] <= 2'b11;
                        2'b11: pht[index] <= 2'b11;
                    endcase
                end else begin
                    case (pht[index])
                        2'b00: pht[index] <= 2'b10;
                        2'b01: pht[index] <= 2'b00;
                        2'b10: pht[index] <= 2'b01;
                        2'b11: pht[index] <= 2'b11;
                    endcase
                end
            end
        end
    end

    assign predict_taken = prediction;
    assign predict_history = predicted_history;

endmodule

