module Lemming (
    input clk,
    input areset,
    input bump_left,
    input bump_right,
    input ground,
    input dig,
    output reg walk_left,
    output reg walk_right,
    output reg aaah,
    output reg digging
);

// State definition
localparam WALK_LEFT = 2'b00;
localparam WALK_RIGHT = 2'b01;
localparam FALL_LEFT = 2'b10;
localparam FALL_RIGHT = 2'b11;

reg [1:0] state, next_state;
reg [4:0] fall_counter;
reg [4:0] dig_counter;

always @(posedge clk or posedge areset) begin
    if (areset) begin
        state <= WALK_LEFT;
        fall_counter <= 0;
        dig_counter <= 0;
    end else begin
        state <= next_state;
        if (!ground && (state == WALK_LEFT || state == WALK_RIGHT)) begin
            fall_counter <= fall_counter + 1;
        end else begin
            fall_counter <= 0;
        end

        if (dig) begin
            dig_counter <= dig_counter + 1;
        end else begin
            dig_counter <= 0;
        end
    end
end

always @(*) begin
    // Default next state
    next_state = state;

    // Update outputs
    walk_left = (state == WALK_LEFT);
    walk_right = (state == WALK_RIGHT);
    aaah = (state == FALL_LEFT || state == FALL_RIGHT);
    digging = (state != FALL_LEFT && state != FALL_RIGHT && dig_counter > 0 && ground);

    // Next state logic
    case (state)
        WALK_LEFT: begin
            if (!ground) begin
                next_state = FALL_LEFT;
            end else if (bump_left && ground) begin
                next_state = WALK_RIGHT;
            end
        end
        WALK_RIGHT: begin
            if (!ground) begin
                next_state = FALL_RIGHT;
            end else if (bump_right && ground) begin
                next_state = WALK_LEFT;
            end
        end
        FALL_LEFT: begin
            if (ground) begin
                next_state = WALK_LEFT;
            end
        end
        FALL_RIGHT: begin
            if (ground) begin
                next_state = WALK_RIGHT;
            end
        end
    endcase

    // Splatter logic
    if (fall_counter >= 20) begin
        walk_left = 0;
        walk_right = 0;
        aaah = 0;
        digging = 0;
    end
end

endmodule

