module top_module(
    input clk,
    input reset,
    input data,
    output reg shift_ena,
    output reg counting,
    input done_counting,
    output reg done,
    input ack
);

    // State definitions
    localparam IDLE = 2'b00;
    localparam SEEK = 2'b01;
    localparam SHIFT = 2'b10;
    localparam WAIT = 2'b11;

    reg [1:0] state, next_state;
    reg [3:0] shift_count;

    // State register
    always @(posedge clk or posedge reset) begin
        if (reset)
            state <= IDLE;
        else
            state <= next_state;
    end

    // Next state logic
    always @(*) begin
        next_state = state;
        case (state)
            IDLE:
                if (data == 1'b1 && shift_count == 4'b0000)
                    next_state = SEEK;
            SEEK:
                if (data == 1'b1)
                    next_state = SHIFT;
            SHIFT:
                if (shift_count == 4'b0100)
                    next_state = WAIT;
            WAIT:
                if (done_counting)
                    next_state = IDLE;
        endcase
    end

    // Output logic
    always @(posedge clk or posedge reset) begin
        if (reset) begin
            shift_ena <= 1'b0;
            counting <= 1'b0;
            done <= 1'b0;
            shift_count <= 4'b0000;
        end else begin
            case (state)
                IDLE: begin
                    shift_ena <= 1'b0;
                    counting <= 1'b0;
                    done <= 1'b0;
                    shift_count <= 4'b0000;
                end
                SEEK: begin
                    if (data == 1'b1)
                        shift_count <= shift_count + 1;
                    else
                        shift_count <= 4'b0000;
                end
                SHIFT: begin
                    shift_ena <= 1'b1;
                    shift_count <= shift_count + 1;
                end
                WAIT: begin
                    shift_ena <= 1'b0;
                    counting <= 1'b1;
                    if (done_counting)
                        done <= 1'b1;
                    else
                        done <= 1'b0;
                end
            endcase
        end
    end

endmodule

