module top_module(
    input clk,
    input load,
    input [511:0] data,
    output reg [511:0] q
);

    reg [511:0] next_state;

    always @(*) begin
        next_state[0] = (q[1] + q[511] + q[0]) % 2;
        next_state[511] = (q[0] + q[510] + q[511]) % 2;

        for (int i = 1; i < 511; i++) begin
            next_state[i] = (q[i-1] + q[i+1] + q[i]) % 2;
        end
    end

