module lemming_fsm (
    input clk,
    input areset,
    input bump_left,
    input bump_right,
    output walk_left,
    output walk_right
);

    reg state, next_state;
    parameter WALK_LEFT = 1'b0, WALK_RIGHT = 1'b1;

    // State register
    always @(posedge clk or posedge areset) begin
        if (areset)
            state <= WALK_LEFT;
        else
            state <= next_state;
    end

    // Next state logic
    always @(*) begin
        case (state)
            WALK_LEFT: begin
                if (bump_left && bump_right)
                    next_state = WALK_RIGHT;
                else if (bump_right)
                    next_state = WALK_RIGHT;
                else
                    next_state = WALK_LEFT;
            end
            WALK_RIGHT: begin
                if (bump_left && bump_right)
                    next_state = WALK_LEFT;
                else if (bump_left)
                    next_state = WALK_LEFT;
                else
                    next_state = WALK_RIGHT;
            end
            default: next_state = WALK_LEFT;
        endcase
    end

    // Output logic
    assign walk_left = state;
    assign walk_right = ~state;

endmodule

