module lemmings (
    input clk,
    input areset,
    input bump_left,
    input bump_right,
    input ground,
    input dig,
    output walk_left,
    output walk_right,
    output aaah,
    output digging
);

    // State encoding
    localparam WALK_LEFT = 2'b00;
    localparam WALK_RIGHT = 2'b01;
    localparam FALL = 2'b10;
    localparam SPLAT = 2'b11;

    reg [1:0] state, next_state;
    reg [4:0] fall_counter;

    always @(posedge clk or posedge areset) begin
        if (areset) begin
            state <= WALK_LEFT;
            fall_counter <= 0;
        end else begin
            state <= next_state;
            if (state == FALL) begin
                fall_counter <= fall_counter + 1;
            end else begin
                fall_counter <= 0;
            end
        end
    end

    always @(*) begin
        next_state = state;
        case (state)
            WALK_LEFT: begin
                if (bump_left) begin
                    next_state = WALK_RIGHT;
                end else if (!ground) begin
                    next_state = FALL;
                end
            end
            WALK_RIGHT: begin
                if (bump_right) begin
                    next_state = WALK_LEFT;
                end else if (!ground) begin
                    next_state = FALL;
                end
            end
            FALL: begin
                if (ground) begin
                    if (fall_counter >= 20) begin
                        next_state = SPLAT;
                    end else if (bump_left || dig) begin
                        next_state = WALK_LEFT;
                    end else if (bump_right || dig) begin
                        next_state = WALK_RIGHT;
                    end
                end else if (bump_left || dig) begin
                    next_state = WALK_LEFT;
                end else if (bump_right || dig) begin
                    next_state = WALK_RIGHT;
                end
            end
            SPLAT: begin
                // Do nothing, stay in SPLAT state
            end
        endcase
    end

    assign walk_left = (state == WALK_LEFT);
    assign walk_right = (state == WALK_RIGHT);
    assign aaah = (state == FALL);
    assign digging = (state == WALK_LEFT || state == WALK_RIGHT) && dig && ground;

endmodule

