module top_module(
    input clk,
    input reset,
    output reg [3:1] ena,
    output reg [15:0] q);

    reg [3:0] q_0, q_1, q_2, q_3;
    wire [3:0] sum_0, sum_1, sum_2, sum_3;
    wire carry_1, carry_2, carry_3;

    // BCD adder lookup table
    function [3:0] bcd_add;
        input [3:0] a;
        input [3:0] b;
        input carry;
        begin
            if (carry)
                bcd_add = (a + b + carry > 9) ? (a + b + carry - 10) : (a + b + carry);
            else
                bcd_add = (a + b > 9) ? (a + b - 10) : a + b;
        end
    endfunction

    // BCD adder implementation
    assign {carry_1, sum_0} = bcd_add(q_0, 4'b0001, 1'b0);
    assign {carry_2, sum_1} = bcd_add(q_1, 4'b0001, carry_1);
    assign {carry_3, sum_2} = bcd_add(q_2, 4'b0001, carry_2);
    assign {_, sum_3} = bcd_add(q_3, 4'b0001, carry_3);

    always @(posedge clk or posedge reset) begin
        if (reset) begin
            q_0 <= 4'b0000;
            q_1 <= 4'b0000;
            q_2 <= 4'b0000;
            q_3 <= 4'b0000;
            ena <= 3'b000;
        end else begin
            q_0 <= sum_0;
            q_1 <= sum_1;
            q_2 <= sum_2;
            q_3 <= sum_3;
            ena[1] <= (q_0 == 4'b1001) && (q_1 != 4'b1001);
            ena[2] <= (q_1 == 4'b1001) && (q_2 != 4'b1001);
            ena[3] <= (q_2 == 4'b1001) && (q_3 != 4'b1001);
        end
    end

    always @(*) begin
        q = {q_3, q_2, q_1, q_0};
    end

