module top_module (
    input clk,
    input areset,
    input x,
    output z
);

    reg [3:0] current_state, next_state;
    reg [3:0] counter, next_counter;
    reg z_next;
    wire z_out;

    localparam INITIAL = 4'b0001;
    localparam PROCESSING = 4'b0010;
    localparam DONE = 4'b0100;

    always @(posedge clk or posedge areset) begin
        if (areset) begin
            current_state <= INITIAL;
            counter <= 0;
        end
        else begin
            current_state <= next_state;
            counter <= next_counter;
        end
    end

    always @(*) begin
        next_state = current_state;
        next_counter = counter;
        z_next = 0;

        case (current_state)
            INITIAL: begin
                if (x) begin
                    next_state = PROCESSING;
                    next_counter = 1;
                end
                z_next = 0;
            end
            PROCESSING: begin
                if (x) begin
                    next_counter = counter + 1;
                end
                z_next = z_out;
            end
            DONE: begin
                if (x) begin
                    next_counter = counter + 1;
                end
                z_next = z_out;
            end
            default: begin
                z_next = 0;
            end
        endcase

        if (next_counter == 4'b1111) begin
            next_state = DONE;
        end

        if (current_state == DONE) begin
            if (x) begin
                next_state = INITIAL;
                next_counter = 0;
            end
        end
    end

    assign z_out = (current_state == PROCESSING) ? ~counter[0] : 0;

    always @(negedge clk) begin
        z <= z_next;
    end

endmodule

