module top_module (
    input clk,
    input areset,
    input bump_left,
    input bump_right,
    output walk_left,
    output walk_right
);

reg [1:0] state, next_state;
parameter WALK_LEFT = 2'b00, WALK_RIGHT = 2'b01;

always @(posedge clk or posedge areset) begin
    if (areset) begin
        state <= WALK_LEFT;
    end else begin
        state <= next_state;
    end
end

always @(*) begin
    case (state)
        WALK_LEFT: begin
            if (bump_left)
                next_state = WALK_RIGHT;
            else if (bump_right)
                next_state = WALK_LEFT;
            else
                next_state = WALK_LEFT;
        end
        WALK_RIGHT: begin
            if (bump_right)
                next_state = WALK_LEFT;
            else if (bump_left)
                next_state = WALK_RIGHT;
            else
                next_state = WALK_RIGHT;
        end
        default: next_state = WALK_LEFT;
    endcase
end

assign walk_left = (state == WALK_LEFT);
assign walk_right = (state == WALK_RIGHT);

endmodule

