module top_module(
    input clk,
    input reset,
    input data,
    output reg start_shifting
);

    // State encoding
    localparam IDLE = 3'b000;
    localparam S1 = 3'b001;
    localparam S11 = 3'b010;
    localparam S110 = 3'b011;
    localparam MATCH = 3'b100;

    reg [2:0] current_state, next_state;

    // State register
    always @(posedge clk or posedge reset) begin
        if (reset)
            current_state <= IDLE;
        else
            current_state <= next_state;
    end

    // Next state logic
    always @(*) begin
        case (current_state)
            IDLE: next_state = (data) ? S1 : IDLE;
            S1: next_state = (data) ? S11 : IDLE;
            S11: next_state = (data) ? S11 : S110;
            S110: next_state = (data) ? MATCH : IDLE;
            MATCH: next_state = MATCH;
            default: next_state = IDLE;
        endcase
    end

    // Output logic
    always @(posedge clk or posedge reset) begin
        if (reset)
            start_shifting <= 0;
        else
            start_shifting <= (current_state == MATCH);
    end

endmodule

