module top_module (
    input clk,
    input j,
    input k,
    input areset,
    output reg out
);

  reg [1:0] state;
  reg [1:0] next_state;

  // State parameters
  parameter OFF = 2'b00;
  parameter ON  = 2'b01;

  // State register
  always @(posedge clk or posedge areset) begin
    if (areset)
      state <= OFF;
    else
      state <= next_state;
  end

  // Next state logic
  always @(*) begin
    case (state)
