module top_module (
    input clk,
    input in,
    input reset,
    output out
);

    reg state, next_state;
    parameter A = 1'b0, B = 1'b1;

    always @(posedge clk or posedge reset) begin
        if (reset)
            state <= B;
        else
            state <= next_state;
    end

    always @(*) begin
        case (state)
            B: next_state = in ? B : A;
            A: next_state = in ? A : B;
            default: next_state = B;
        endcase
    end

    assign out = (state == A) ? 1'b0 : 1'b1;

