module top_module (
    input a,
    input b,
    input cin,
    output reg cout,
    output sum
);

    wire cin_g;
    reg sum_g, sum_g_next;

    // Carry-in generation
    assign cin_g = a & b;

    // Sum generation
    always @(*) begin
        sum_g_next = a ^ b ^ cin;
    end

    // Carry-out generation
    always @(posedge cin_g) begin
        sum_g <= sum_g_next;
    end

    // Assign sum output
    assign sum = sum_g;

    // Assign cout output
    always @(*) begin
        cout = (a ^ b ^ cin) & cin_g;
    end

endmodule

