module top_module (
    input clk,
    input in,
    input reset,
    output reg done
);

    // State encoding
    localparam IDLE = 3'd0;
    localparam START = 3'd1;
    localparam DATA = 3'd2;
    localparam STOP = 3'd3;
    localparam WAIT_STOP = 3'd4;

    reg [2:0] state, next_state;
    reg [3:0] bit_count;
    reg [7:0] data_reg;

    always @(posedge clk or posedge reset) begin
        if (reset) begin
            state <= IDLE;
            bit_count <= 0;
            data_reg <= 0;
            done <= 0;
        end else begin
            state <= next_state;
            if (state == DATA && in != data_reg[bit_count])
                data_reg <= {data_reg[6:0], in};
            if (state == DATA && in == data_reg[bit_count])
                bit_count <= bit_count + 1;
            if (state == IDLE)
                bit_count <= 0;
            if (state == STOP)
                done <= 1;
            else
                done <= 0;
        end
    end

    always @(*) begin
        next_state = state;
        case (state)
            IDLE: begin
                if (in == 0)
                    next_state = START;
            end
            START: begin
                next_state = DATA;
            end
            DATA: begin
                if (bit_count == 7)
                    next_state = STOP;
            end
            STOP: begin
                if (in == 1)
                    next_state = IDLE;
                else
                    next_state = WAIT_STOP;
            end
            WAIT_STOP: begin
                if (in == 1)
                    next_state = IDLE;
            end
        endcase
    end

endmodule

