  reg [7:0] byte_reg;
  reg [3:0] bit_count;

  // State transition logic
  always @(posedge clk or posedge reset) begin
    if (reset)
      state <= IDLE;
    else
      state <= next_state;
  end

  // Next state logic
  always @(*) begin
    case (state)
      IDLE: next_state = (in == 0) ? START : IDLE;
