module gshare_branch_predictor(
    input clk,
    input areset,

    input predict_valid,
    input [6:0] predict_pc,
    output predict_taken,
    output [6:0] predict_history,

    input train_valid,
    input train_taken,
    input train_mispredicted,
    input [6:0] train_history,
    input [6:0] train_pc
);

    reg [6:0] global_history;
    reg [6:0] pattern_history_table [127:0];
    reg [1:0] counter_table [127:0];

    wire [6:0] index;
    wire [1:0] counter_value;
    reg [1:0] next_counter_value;

    assign index = predict_pc ^ global_history;
    assign counter_value = counter_table[index];

    assign predict_taken = counter_value[1];
    assign predict_history = global_history;

    always @(posedge clk or posedge areset) begin
        if (areset) begin
            global_history <= 7'b0;
        end else if (train_valid) begin
            global_history <= {global_history[5:0], train_taken};
        end
    end

    always @(posedge clk) begin
        if (train_valid) begin
            pattern_history_table[index] <= train_history;
            counter_table[index] <= next_counter_value;
        end
    end

    always @(*) begin
        if (train_valid) begin
            if (train_taken == counter_value[1]) begin
                if (train_taken) begin
                    next_counter_value = (counter_value == 2'b11) ? 2'b11 : counter_value + 1'b1;
                end else begin
                    next_counter_value = (counter_value == 2'b00) ? 2'b00 : counter_value - 1'b1;
                end
            end else begin
                next_counter_value = counter_value;
            end
        end else begin
            next_counter_value = counter_value;
        end
    end

endmodule

