module top_module (
    input clk,
    input areset,
    input bump_left,
    input bump_right,
    input ground,
    input dig,
    output walk_left,
    output walk_right,
    output aaah,
    output digging
);

    // Declare state variables
    reg [1:0] current_state, next_state;
    reg [4:0] counter;
    reg splat;

    // State parameters
    localparam WALK_LEFT = 2'b00;
    localparam WALK_RIGHT = 2'b01;
    localparam FALL = 2'b10;

    // Output assignments
    assign walk_left = (current_state == WALK_LEFT);
    assign walk_right = (current_state == WALK_RIGHT);
    assign aaah = (current_state == FALL);
    assign digging = (current_state == WALK_LEFT || current_state == WALK_RIGHT) && ground;

    // State machine logic
    always @(posedge clk or posedge areset) begin
        if (areset) begin
            current_state <= WALK_LEFT;
            counter <= 0;
            splat <= 0;
        end else begin
            current_state <= next_state;
            if (current_state == FALL) begin
                counter <= counter + 1;
                if (counter == 20) begin
                    splat <= 1;
                end
            end else begin
                counter <= 0;
            end
        end
    end

    // Next state logic
    always @(*) begin
        if (splat) begin
            next_state = WALK_LEFT;
        end else begin
            case (current_state)
                WALK_LEFT: begin
                    if (bump_left) begin
                        next_state = WALK_RIGHT;
                    end else if (!ground) begin
                        next_state = FALL;
                    end else if (dig && ground) begin
                        next_state = WALK_LEFT;
                    end else begin
                        next_state = WALK_LEFT;
                    end
                end
                WALK_RIGHT: begin
                    if (bump_right) begin
                        next_state = WALK_LEFT;
                    end else if (!ground) begin
                        next_state = FALL;
                    end else if (dig && ground) begin
                        next_state = WALK_RIGHT;
                    end else begin
                        next_state = WALK_RIGHT;
                    end
                end
                FALL: begin
                    if (ground) begin
                        if (bump_left && bump_right) begin
                            next_state = WALK_RIGHT;
                        end else if (bump_left) begin
                            next_state = WALK_LEFT;
                        end else if (bump_right) begin
                            next_state = WALK_RIGHT;
                        end else begin
                            next_state = current_state;
                        end
                    end else begin
                        next_state = FALL;
                    end
                end
                default: begin
                    next_state = WALK_LEFT;
                end
            endcase
        end
    end

endmodule

