## Solution
module top_module(
    input clk,
    input load,
    input [255:0] data,
    output reg [255:0] q
);

    function [2:0] count_alive;
        input [255:0] grid;
        input [7:0] x, y;
        reg [8:0] count;
        integer i, j;
        begin
            count = 0;
            for (i = -1; i <= 1; i = i + 1) begin
                for (j = -1; j <= 1; j = j + 1) begin
                    if ((i != 0 || j != 0) && (grid[(x+i+15)%16 + ((y+j+15)%16)*16] == 1))
                        count = count + 1;
                end
            end
            count_alive = count[2:0];
        end
    endfunction

    always @(posedge clk) begin
        if (load)
            q <= data;
        else begin
            reg [255:0] next_q;
            for (integer i = 0; i < 16; i = i + 1) begin
                for (integer j = 0; j < 16; j = j + 1) begin
                    case (count_alive(q, i, j))
                        0: next_q[(i+15)*16+j+15] = 0;
                        1, 2: next_q[(i+15)*16+j+15] = q[(i+15)*16+j+15];
                        3: next_q[(i+15)*16+j+15] = 1;
                        4, 5, 6, 7, 8: next_q[(i+15)*16+j+15] = 0;
                    endcase
                end
            end
            q <= next_q;
        end
    end

endmodule

