module top_module(
    input d,
    input done_counting,
    input ack,
    input [9:0] state,    // 10-bit one-hot current state
    output B3_next,
    output S_next,
    output S1_next,
    output Count_next,
    output Wait_next,
    output done,
    output counting,
    output shift_ena
);

    // State encodings
    localparam S     = 10'b0000000001;
    localparam S1    = 10'b0000000010;
    localparam S11   = 10'b0000000100;
    localparam S110  = 10'b0000001000;
    localparam B0    = 10'b0000010000;
    localparam B1    = 10'b0000100000;
    localparam B2    = 10'b0001000000;
    localparam B3    = 10'b0010000000;
    localparam Count = 10'b0100000000;
    localparam Wait  = 10'b1000000000;

    // Next-state logic
    assign S_next    = (state == S     && d == 1'b0) ? S     :
                       (state == S1    && d == 1'b1) ? S1    :
                       (state == S11   && d == 1'b0) ? S     :
                       (state == S110  && d == 1'b0) ? S     :
                       (state == S11   && d == 1'b1) ? S11   :
                       (state == S110  && d == 1'b1) ? S11   :
                       (state == B3    && d == 1'b1) ? Count :
                       (state == Count && !done_counting) ? Count :
                       (state == Wait  && ack == 1'b0) ? Wait  :
                       (state == Wait  && ack == 1'b1) ? S     :
                       state;

    assign S1_next   = (state == S1    && d == 1'b1) ? S1    : S;
    assign B3_next   = (state == B3    && d == 1'b1) ? B0    : S;
    assign Count_next = (state == Count && !done_counting) ? Count :
                        (state == Wait  && ack == 1'b1) ? S : state;
    assign Wait_next = (state == Wait  && ack == 1'b1) ? S : state;

    // Output logic
    assign done = (state == Wait && ack == 1'b1) ? 1'b1 : 1'b0;
    assign counting = (state == Count) ? 1'b1 : 1'b0;
    assign shift_ena = (state == B0 || state == B1 || state == B2 || state == B3) ? 1'b1 : 1'b0;

endmodule

