module moore_machine (
    input clk,
    input in,
    input areset,
    output reg out
);

    reg [1:0] state;
    parameter A = 2'b00, B = 2'b01;

    always @(posedge clk or posedge areset) begin
        if (areset) begin
            state <= B;
            out <= 1'b0;
        end
        else begin
            case (state)
                A: begin
                    if (in == 1'b0) begin
                        state <= B;
                        out <= 1'b0;
                    end
                    else begin
                        state <= A;
                        out <= 1'b0;
                    end
                end
                B: begin
                    if (in == 1'b1) begin
                        state <= B;
                        out <= 1'b1;
                    end
                    else begin
                        state <= A;
                        out <= 1'b0;
                    end
                end
            endcase
        end
    end

