module fsm_arbiter (
    input clk,
    input resetn,
    input [3:1] r,
    output [3:1] g
);

    // State encoding
    localparam A = 2'b00;
    localparam B = 2'b01;
    localparam C = 2'b10;
    localparam D = 2'b11;

    // State register
    reg [1:0] current_state, next_state;

    // State table
    always @(*) begin
        case (current_state)
            A: begin
                if (r[1]) next_state = B;
                else if (r[2]) next_state = C;
                else if (r[3]) next_state = D;
                else next_state = A;
            end
            B: begin
                if (r[1]) next_state = B;
                else next_state = A;
            end
            C: begin
                if (r[2]) next_state = C;
                else next_state = A;
            end
            D: begin
                if (r[3]) next_state = D;
                else next_state = A;
            end
            default: next_state = A;
        endcase
    end

    // State flip-flops
    always @(posedge clk or negedge resetn) begin
        if (!resetn)
            current_state <= A;
        else
            current_state <= next_state;
    end

    // Output logic
    assign g[1] = (current_state == B);
    assign g[2] = (current_state == C);
    assign g[3] = (current_state == D);

endmodule

